// Menu e Rota

// importaçao do React
import React from "react";

// importaçao da rota 
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";

// importaçao do Bootstrap
import { 
  Nav,
  Navbar 
} from 'react-bootstrap';

// importaçao dos aquivos para ser ultilizado no menu 

import Embarque from '../Pages/Embarque/embarque';
import Registro from '../Pages/Registro/registro';
import Login from '../Pages/Login/login';
import Cometa from '../Imagem/cometa.png';

// importaçao do estilo do Menu 

import './menu.css';

// NavBar e Rotas

export default function Menu() {
  return (
    <Router>
      <div>
      <Navbar className="nav" collapseOnSelect expand="lg" variant="dark">
      <img 
        src={Cometa}
        alt="Viação Cometa"
        width="200"
        height="50"
        className="d-inline-block align-top"
        />
          <Navbar.Brand>Viação Cometa</Navbar.Brand>
          <Navbar.Toggle aria-controls="responsive-navbar-nav" />
          <Navbar.Collapse id="responsive-navbar-nav">
            <Nav className="mr-auto">
              <Link to="/" className="je">Login</Link>
              <Link to="/registro" className="a">Registro</Link>
              <Link to="/embarque" className="a">Embarques</Link>
            </Nav>
          </Navbar.Collapse>
        </Navbar>

        {/* Direcionamento para as paginas */}

        <Switch>
          <Route path="/embarque">
            <Embarque />
          </Route>
          <Route path="/registro">
            <Registro />
          </Route>
          <Route path="/">
            <Login />
          </Route>
        </Switch>
      </div>
    </Router>
  );
}
